/* V2EX风格自定义样式 */
:root {
  --v2ex-bg: #f7f7f7;
  --v2ex-white: #ffffff;
  --v2ex-border: #e2e2e2;
  --v2ex-text: #333333;
  --v2ex-gray: #999999;
  --v2ex-blue: #778087;
  --v2ex-hover: #f5f5f5;
  --v2ex-shadow: 0 2px 3px rgba(0,0,0,0.1);
}

/* 整体背景 */
body {
  background-color: var(--v2ex-bg) !important;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif !important;
  line-height: 1.6 !important;
}

/* 头部导航 */
.navbar {
  background-color: var(--v2ex-white) !important;
  border-bottom: 1px solid var(--v2ex-border) !important;
  box-shadow: var(--v2ex-shadow) !important;
}

.navbar-brand {
  color: var(--v2ex-text) !important;
  font-weight: 600 !important;
}

.navbar-nav .nav-link {
  color: var(--v2ex-blue) !important;
  font-size: 14px !important;
  padding: 8px 15px !important;
  border-radius: 3px !important;
  margin: 0 2px !important;
}

.navbar-nav .nav-link:hover {
  background-color: var(--v2ex-hover) !important;
  color: var(--v2ex-text) !important;
}

/* 主要内容区域 */
.container, .container-fluid {
  background-color: transparent !important;
}

/* 卡片样式 - 模仿V2EX的帖子框 */
.card, .post-item {
  background-color: var(--v2ex-white) !important;
  border: 1px solid var(--v2ex-border) !important;
  border-radius: 6px !important;
  box-shadow: var(--v2ex-shadow) !important;
  margin-bottom: 15px !important;
}

.card-header {
  background-color: var(--v2ex-hover) !important;
  border-bottom: 1px solid var(--v2ex-border) !important;
  font-size: 14px !important;
  color: var(--v2ex-gray) !important;
}

.card-body {
  padding: 15px !important;
}

/* 文章列表样式 */
.post-title {
  color: var(--v2ex-text) !important;
  font-size: 16px !important;
  font-weight: 500 !important;
  line-height: 1.4 !important;
  text-decoration: none !important;
}

.post-title:hover {
  color: var(--v2ex-blue) !important;
  text-decoration: none !important;
}

.post-meta {
  font-size: 12px !important;
  color: var(--v2ex-gray) !important;
  margin-top: 8px !important;
}

.post-meta a {
  color: var(--v2ex-gray) !important;
  text-decoration: none !important;
}

.post-meta a:hover {
  color: var(--v2ex-blue) !important;
}

/* 标签样式 */
.badge, .tag {
  background-color: var(--v2ex-hover) !important;
  color: var(--v2ex-blue) !important;
  font-size: 12px !important;
  font-weight: normal !important;
  padding: 4px 8px !important;
  border-radius: 3px !important;
  border: 1px solid var(--v2ex-border) !important;
  text-decoration: none !important;
  margin-right: 5px !important;
}

.badge:hover, .tag:hover {
  background-color: var(--v2ex-border) !important;
  color: var(--v2ex-text) !important;
}

/* 按钮样式 */
.btn-primary {
  background-color: var(--v2ex-blue) !important;
  border-color: var(--v2ex-blue) !important;
  font-size: 14px !important;
}

.btn-outline-primary {
  color: var(--v2ex-blue) !important;
  border-color: var(--v2ex-border) !important;
  font-size: 14px !important;
}

.btn-outline-primary:hover {
  background-color: var(--v2ex-hover) !important;
  color: var(--v2ex-text) !important;
}

/* 分页样式 */
.pagination .page-link {
  color: var(--v2ex-blue) !important;
  border-color: var(--v2ex-border) !important;
  background-color: var(--v2ex-white) !important;
}

.pagination .page-item.active .page-link {
  background-color: var(--v2ex-blue) !important;
  border-color: var(--v2ex-blue) !important;
}

/* 侧边栏样式 */
.sidebar .card {
  border-radius: 6px !important;
}

.list-group-item {
  border-color: var(--v2ex-border) !important;
  background-color: var(--v2ex-white) !important;
}

.list-group-item:hover {
  background-color: var(--v2ex-hover) !important;
}

/* 文章内容样式 */
.post-content {
  color: var(--v2ex-text) !important;
  line-height: 1.8 !important;
}

.post-content h1, 
.post-content h2, 
.post-content h3, 
.post-content h4, 
.post-content h5, 
.post-content h6 {
  color: var(--v2ex-text) !important;
  margin-top: 24px !important;
  margin-bottom: 16px !important;
}

.post-content code {
  background-color: var(--v2ex-hover) !important;
  color: var(--v2ex-text) !important;
  padding: 2px 4px !important;
  border-radius: 3px !important;
  font-size: 0.9em !important;
}

.post-content pre {
  background-color: var(--v2ex-hover) !important;
  border: 1px solid var(--v2ex-border) !important;
  border-radius: 6px !important;
  padding: 15px !important;
}

/* 评论区样式 */
.comment {
  border-bottom: 1px solid var(--v2ex-border) !important;
  padding: 15px 0 !important;
}

.comment:last-child {
  border-bottom: none !important;
}

/* 响应式调整 */
@media (max-width: 768px) {
  .card-body {
    padding: 10px !important;
  }

  .post-title {
    font-size: 15px !important;
  }
}
